mapper.select:
select m.ordertitle_code ordertitle_number, t.id, t.oredertitle_code, t.goods_id, t.goods_name, t.category_id, t.category_name, t.goods_num, t.price, t.money, t.quote_id, t.exe_sendgoods_num, t.exe_getgoods_num, t.exe_returngoods_num, t.exe_getreturngoods_num, t.exe_paymoney_num, t.exe_getmoney_num, t.exe_refund_num, t.exe_getrefund_num, t.sendgoods_num, t.getgoods_num, t.returngoods_num, t.getreturngoods_num, t.paymoney_num, t.getmoney_num, t.refund_num, t.getrefund_num, t.lockmoney_num, t.buyers_id, t.buyers_name, t.sellers_id, t.sellers_name, t.status, t.remark, t.workflow_type,t.stop_status,t.execute_status from mt_order t join mt_ordertitle m on t.oredertitle_code = m.id
and t.buyers_id = #{buyersId,jdbcType=VARCHAR} and t.sellers_id = #{sellersId,jdbcType=VARCHAR} and m.ordertitle_code = #{ordertitleNumber,jdbcType=INTEGER} and t.goods_name = #{goodsName,jdbcType=VARCHAR} and t.exe_paymoney_num > 0 and t.execute_status = 1 and t.status = 4
order by m.ordertitle_code limit #{startFirst},#{startEnd}

select

from mt_order
where exe_paymoney_num > 0
and buyers_id = #{buyersId,jdbcType=VARCHAR}
and sellers_id = #{sellersId,jdbcType=VARCHAR}
and workflow_type = 2

